import axios from 'axios';
import React, { Component } from 'react'
class LoginComponent extends Component{
    constructor(props){
        super(props)
        this.state={
            users:[],
            username:'',
            password:'',
            hasFailed:false
            
        }
    }
    handleWelcome=()=>{
        axios.get("http://localhost:8080/myusers")
        .then(response=>{this.setState({users:response.data})})
    }
    componentDidMount(){
        this.handleWelcome()
    }
    handleLogin=()=>{
        // console.log(e.)
        this.state.users.map(user=>{

            if(this.state.username===user.user_name && this.state.password===user.password){ 
                alert('Login Successful')
                this.setState({hasFailed:false})
                // console.log(this.state.SuccessMsg)
                // console.log(user.user +" "+ user.password)
                // console.log(this.state)
            }else{
                console.log(this.state.username+" "+this.state.password)
                this.setState({hasFailed:true})
                console.log("Login Failed")
            }
        }
        )
        // this.props.history.push("/users")
        // axios.post("http://localhost:8080/users",)
    }
    handleChange=(event)=>{
        this.setState({[event.target.name]:event.target.value})
    }
    handleReset=()=>{
        this.setState({username:'',password:''})
    }
    render(){
        return(
            <div>
                    <h1>Welcome to Task Management System</h1>
                    <h2><em>Login Here....</em></h2>
                    {this.state.hasFailed && <h1>Invalid Credentials</h1>}
                    Name:<input type='text' name='username' placeholder='Enter name here' value={this.state.username} onChange={this.handleChange}/><br/>
                    <br/>Password:<input type='password' name='password' placeholder='Enter password here' value={this.state.password} onChange={this.handleChange}/><br></br>
                    <br/><button onClick={this.handleLogin} className='btn btn-outline-dark'>Submit</button>
                    <button onClick={this.handleReset} className='btn btn-outline-warning'>Reset</button>

            </div>
        );
    }
}
export default LoginComponent;
