import axios from "axios";
import React, { Component } from "react";
import AuthenticationService from "./AuthenticationService";
class UpdateTaskDemo extends Component{
    constructor(props){
        super(props);
        this.state={
            taskid:this.props.match.params.taskId,
            utask:{
                status:'',
                priority:'',
                notes:''
            },
            task1:{},
            status:'',
            priority:'',
            notes:''
        }
    }
    // handleChange=(e)=>{
    //     this.setState({[e.target.name]:e.target.value})
    // }
    handleTasks=()=>{
        let task=this.state.taskid
        axios.get(`http://localhost:8080/allTasks/${task}`)
        .then(response=>{console.log(response.data);this.setState({task1:response.data})})

    }
    componentDidMount(){
        this.handleTasks();
    }
    handleSubmit=()=>{
        this.setState(prevState => {
            let utask = Object.assign({}, prevState.utask);  // creating copy of state variable jasper
            utask.status = this.state.status;                     // update the name property, assign a new value                 
            utask.priority=this.state.priority;
            utask.notes=this.state.notes;
            return { utask };                                 // return new object jasper object
          })
        console.log(this.state.utask)
        axios.put(`http://localhost:8080/tasks/${this.state.taskid}`,this.state.utask)
        .then(response=>{alert(response.data)})

        this.props.history.push(`/tasks/${AuthenticationService.getUserId()}`)
        window.location.reload()

        // this.handleClear()
    }
    // handleClear=()=>{
    //     this.setState(prevState => {
    //         let utask = Object.assign({}, prevState.utask); 
    //         utask.status = '';                   
    //         utask.priority='';
    //         utask.notes='';
    //         return { utask };
    //       })
    // }
    handleChange=(e)=>{
        this.setState({[e.target.name]:e.target.value})
        console.log(e.target.name)
        console.log(e.target.value)
    }
    render(){
        
        return(
            <div>
                <h1><em>Update Your Task Here...{this.state.taskid}</em></h1>
                <div className='container'>
                {/* {console.log(status)} */}
                                    <br/>
                                    {/* {console.log(creatorId)} */}
                                    <fieldset className='form-group'>
                                        <label>Creator ID</label>
                                        <input type='number' className='form-control' name='creatorId' value={this.state.task1.creatorId} readOnly/>
                                    </fieldset>
                                    <br/>
                                    <fieldset className='form-group'>
                                        <label>Task Name</label>
                                        <input type='text' className='form-control' name='name' value={this.state.task1.name} readOnly/>
                                    </fieldset>
                                    <br/>
                                    <fieldset className='form-group'>
                                        <label>Task Description</label>
                                        <input type='text' className='form-control' name='description' value={this.state.task1.description} readOnly/>
                                    </fieldset>
                                    <br/>
                                    <fieldset className='form-group'>
                                        <label>Status Update</label>
                                        <select name="status" className='form-control' value={this.state.task1.status} onChange={this.handleChange} required>
                                                <option value="">select--</option>
                                                <option value="In progress">In progress</option>
                                                <option value="Completed">Completed</option>
                                        </select>   
                                    </fieldset>
                                    <br/>
                                    <fieldset className='form-group'>
                                        <label>Priority Update</label>
                                        <select name="priority" className='form-control' value={this.state.task1.priority} onChange={this.handleChange} required>
                                                <option value="">select--</option>
                                                <option value="High">High</option>
                                                <option value="Medium">Medium</option>
                                                <option value="Low">Low</option>
                                        </select>   
                                    </fieldset>
                                    <br/>
                                    <fieldset className='form-group'>
                                        <label>Add Notes</label>
                                        <textarea  name='notes' className='form-control' value={this.state.task1.notes} onChange={this.handleChange} required/>
                                    </fieldset>
                                    <br/>
                                    <button type='submit' className='btn btn-outline-success' style={{'margin':'20px'}}>Update</button>

                </div>
            </div>
        );
    }
} 
export default UpdateTaskDemo;
