import axios from "axios";
import React, { Component } from "react";
import {Formik,Field,Form} from 'formik'
class AddTask extends Component{
    constructor(props){
        super(props)
        this.state={
            task:{}
        }
    }
    handleSubmit=(values)=>{
        console.log(values)
    }
    render(){
        return(
            <div>
                <Formik
                    initialValues={{status:'New'}}
                    onSubmit={this.handleSubmit}
                >
                    {
                        (props)=>
                        <Form>
                            <fieldset className='form-group'>
                                <label>Creator Id</label>
                                <Field className='form-control' type='number' name='creatorId' required/>
                            </fieldset>
                            <br/>
                            <fieldset className='form-group'>
                                <label>Task Name</label>
                                <Field className='form-control' type='text' name='name' required/>
                            </fieldset>
                            <br/>
                            <fieldset className='form-group'>
                                <label>Task Description</label>
                                <Field as='textarea' className='form-control' type='text' name='description' required/>
                            </fieldset>
                            <br/>
                            <fieldset className='form-group'>
                                <label>Assign To</label>
                                <Field className='form-control' type='number' name='ownerId' required/>
                            </fieldset>
                            <br/>
                            <fieldset className='form-group'>
                                <label>Status</label>
                                <Field className='form-control' type='text' name='status' readOnly/>
                            </fieldset>
                            <br/>
                            <fieldset className='form-group'>
                                <label>Priority</label>
                                <Field as="select" name="priority" className='form-control'>
                                    <option value="">select--</option>
                                    <option value="High">High</option>
                                    <option value="Medium">Medium</option>
                                    <option value="Low">Low</option>
                                </Field>   
                            </fieldset>
                            <br/>
                            <button type='submit' className='btn btn-outline-success' style={{'margin':'20px'}}>Create</button>
                            <button type='reset' className='btn btn-outline-warning'>Reset</button>
                        </Form>
                    }
                </Formik>
            </div>
        );
    }
}
export default AddTask;
