import axios from "axios";
import React, { Component } from "react";
import {moment} from "moment";
class UpdateTask extends Component{
    constructor(props){
        super(props);
        this.state={
            taskid:this.props.match.params.taskId,
            utask:{
                status:'',
                priority:'',
                notes:'',
            },
            task1:{},
        }
    }
    handleChange=(e)=>{
        this.setState({[e.target.name]:e.target.value})
    }
    handleTasks=()=>{
        let task=this.state.taskid
        axios.get(`http://localhost:8080/allTasks/${task}`)
        .then(response=>{console.log(response.data);this.setState({task1:response.data})})
    }
    componentDidMount(){
        this.handleTasks();
    }
    getDate=()=>{
        return moment();
    }
    render(){
        return(
            <div>
                <h1><em>Update Your Task Here...{this.state.taskid}</em></h1>
                <center>
                <table>
                    <tbody>
                        <tr>
                            <td><label htmlFor='creator_id'>Creator Id: </label></td>
                            <td><input type='text' name='creator_id' value={this.state.task1.creatorId}/>(cannot be modified)</td>
                        </tr>
                        <tr>
                            <td><label htmlFor='name'>Task Name: </label></td>
                            <td><input type='text' name='name' value={this.state.task1.name}/>(cannot be modified)</td>
                        </tr>
                        <tr>
                            <td><label htmlFor='description'>Task Description: </label></td>
                            <td><textarea name='description' value={this.state.task1.description}/>(cannot be modified)</td>
                        </tr>
                        <tr>
                            <td><label htmlFor='status'>Status Update: </label></td>
                            <td>
                                <select name='status' onChange={this.handleChange}>
                                        <option value=''>select--</option>
                                        <option value='New'>New</option>
                                        <option value='In progress'>In progress</option>
                                        <option value='Completed'>Completed</option>
                                </select>
                            </td>
                        </tr>
                    </tbody>
                </table>
                </center>
            </div>
        );
    }
} 
export default UpdateTask;
