import axios from "axios";
import React, { Component } from "react";
class AddTask extends Component{
    constructor(props){
        super(props)
        this.state={
            task:{creator_id:'',name:'',created_on:new Date()}
        }
    }
    handleSubmit=(e)=>{
        alert('Form submitted successfully')

        console.log(e)
    }
    handleChange=(e)=>{
        this.setState({[e.target.name]:e.target.value})
    }
    render(){
        return(
            <div style={{'textAlign':'center'}}>
                <h1>Create Task Here...</h1>
                <form onSubmit={this.handleSubmit}>
                    <table>
                        <tr>
                            <td><label htmlFor='cid'>CreatorId:</label></td>
                            <td><input type='text' name='creator_id' id='cid' onChange={this.handleChange} required/></td>
                        </tr>
                        <tr>
                            <td><label htmlFor='tname'>Task Name:</label></td>
                            <td><input type='text' name='name' id='tname' required/></td>
                        </tr>
                        <tr>
                            <td><label htmlFor='tdesc'>Task Description:</label></td>
                            <td><textarea type='text' name='description' id='tdesc' onChange={this.handleChange} required/></td>
                        </tr>

                        <tr><td colSpan='2'><input type='submit' name='submit' value='Assign'/></td></tr>
                    </table>
                </form>
            </div>
        );
    }
}
export default AddTask;
