import React, { Component } from "react";
import axios from 'axios';
class GetTasks extends Component{
    constructor(props){
        super(props)
        this.state={
            tasks:[]
        }
    }
    componentDidMount(){
        axios.get('http://localhost:8080/tasks')
        .then(response=>this.setState({tasks:response.data}))
    }
    // handleClick=()=>{
    //     axios.get('https://api.github.com/users/maecapozzi')
    //     .then(response=>this.setState({name:response.data.name}))
    // }
    render(){
        return(
            <div className='container-fluid'>
                <h1>List of Tasks</h1>
                    <table className='table table-striped'>
                        <thead>
                            <tr>
                                <th>ownerId</th>
                                <th>creatorId</th>
                                <th>name</th>
                                <th>description</th>
                                <th>status</th>
                                <th>priority</th>
                                <th>notes</th>
                                <th>isBookmarked</th>
                                <th>createdOn</th>
                                <th>statusChangedOn</th>
                            </tr>
                        </thead>
                        <tbody>
                        {this.state.tasks.map(task=>
                            <tr key={task.taskId}>
                                <td>{task.ownerId}</td>
                                <td>{task.creatorId}</td>
                                <td>{task.name}</td>
                                <td>{task.description}</td>
                                <td>{task.status}</td>
                                <td>{task.priority}</td>
                                <td>{task.notes}</td>
                                <td>{task.isBookmarked}</td>
                                <td>{task.createdOn}</td>
                                <td>{task.statusChangedOn}</td>
                            </tr>
                        )}
                        </tbody>
                    </table>
                    {/* <button onClick={this.handleClick}>Click Me!</button>
                    <h1>{this.state.name}</h1> */}
            </div>
        );
    }
} 
export default GetTasks;
