import React, { Component } from "react";
import axios from 'axios';
import { Link } from "react-router-dom";
class GetTasks extends Component{
    constructor(props){
        super(props)
        this.state={
            tasks:[],
            ownerId:this.props.match.params.user_id
        }
    }
    handleTask=()=>{
        let owner_id=this.state.ownerId;
        axios.get(`http://localhost:8080/tasks/${owner_id}`)
        .then(response=>this.setState({tasks:response.data}))
    }
    componentDidMount(){
        this.handleTask()

    }
    render(){
        return(
            <div className='container-fluid' style={{'padding':'30px'}}>
                <h1>List of Tasks</h1>
                    <div style={{'textAlign':'right','margin':'20px'}}><Link to='/addTask' style={{'textDecoration':'none','color':'black'}}>Add task here...</Link></div>
                    <table className='table table-striped'>
                        <thead>
                            <tr>
                                {/* <th>ownerId</th> */}
                                {/* <th>creatorId</th> */}
                                <th>name</th>
                                <th>description</th>
                                <th>status</th>
                                <th>priority</th>
                                {/* <th>notes</th> */}
                                {/* <th>isBookmarked</th> */}
                                {/* <th>createdOn</th> */}
                                {/* <th>statusChangedOn</th> */}
                            </tr>
                        </thead>
                        <tbody>
                        {this.state.tasks.map(task=>
                            <tr key={task.taskId}>
                                {/* <td>{task.ownerId}</td> */}
                                {/* <td>{task.creatorId}</td> */}
                                <td>{task.name}</td>
                                <td>{task.description}</td>
                                <td>{task.status}</td>
                                <td>{task.priority}</td>
                                {/* <td>{task.notes}</td> */}
                                {/* <td>{task.isBookmarked}</td> */}
                                {/* <td>{task.createdOn}</td> */}
                                {/* <td>{task.statusChangedOn}</td> */}
                            </tr>
                        )}
                        </tbody>
                    </table>
                    {/* <button onClick={this.handleClick}>Click Me!</button>
                    <h1>{this.state.name}</h1> */}
            </div>
        );
    }
} 
export default GetTasks;
