import axios from "axios";
import React, { Component } from "react";
import {moment} from "moment";
import {Field, Form, Formik} from 'formik';
class UpdateTask extends Component{
    constructor(props){
        super(props);
        this.state={
            taskid:this.props.match.params.taskId,
            utask:{
                status:'',
                priority:'',
                notes:'',
                statusChangedOn:new Date().getDate()
            },
            statusChangedOn:new Date(),
            task1:{},
        }
    }
    handleChange=(e)=>{
        this.setState(prevState => {
            let utask = Object.assign({}, prevState.utask);  // creating copy of state variable jasper
            utask.status = 'someothername';                     // update the name property, assign a new value                 
            return { jasper };                                 // return new object jasper object
          })
        let ut=
        this.setState({[e.target.name]:e.target.value})
    }
    handleTasks=()=>{
        let task=this.state.taskid
        axios.get(`http://localhost:8080/allTasks/${task}`)
        .then(response=>{console.log(response.data);this.setState({task1:response.data})})
    }
    componentDidMount(){
        this.handleTasks();
    }
    getDate=()=>{
        return moment();
    }
    render(){
        return(
            <div>
                <h1><em>Update Your Task Here...{this.state.taskid}</em></h1>
                {/* <center>
                <table>
                    <tbody>
                        <tr>
                            <td><label htmlFor='creator_id'>Creator Id: </label></td>
                            <td><input type='text' name='creator_id' value={this.state.task1.creatorId}/>(cannot be modified)</td>
                        </tr>
                        <tr>
                            <td><label htmlFor='name'>Task Name: </label></td>
                            <td><input type='text' name='name' value={this.state.task1.name}/>(cannot be modified)</td>
                        </tr>
                        <tr>
                            <td><label htmlFor='description'>Task Description: </label></td>
                            <td><textarea name='description' value={this.state.task1.description}/>(cannot be modified)</td>
                        </tr>
                        <tr>
                            <td><label htmlFor='status'>Status Update: </label></td>
                            <td>
                                <select name='status' onChange={this.handleChange}>
                                        <option value=''>select--</option>
                                        <option value='New'>New</option>
                                        <option value='In progress'>In progress</option>
                                        <option value='Completed'>Completed</option>
                                </select>
                            </td>
                        </tr>
                    </tbody>
                </table>
                </center> */}
                <div className='container'>
                    <Formik>
                        {
                            (props)=>(
                                <Form >
                                    <fieldset className='form-group'>
                                        <label>Creator ID</label>
                                        <Field type='number' className='form-control' name='creatorId'/>
                                    </fieldset>
                                    <fieldset className='form-group'>
                                        <label>Task Name</label>
                                        <Field type='text' className='form-control' name='name'/>
                                    </fieldset>
                                    <fieldset className='form-group'>
                                        <label>Task Description</label>
                                        <Field type='text' className='form-control' name='description'/>
                                    </fieldset>
                                    <fieldset className='form-group'>
                                        <label>Status Update</label>
                                        <Field as="select" name="status">
                                                <option value="">select--</option>
                                                <option value="New">New</option>
                                                <option value="In progress">In progress</option>
                                                <option value="Completed">Completed</option>
                                        </Field>   
                                    </fieldset>
                                    <fieldset className='form-group'>
                                        <label>Priority Update</label>
                                        <Field as="select" name="status">
                                                <option value="">select--</option>
                                                <option value="High">High</option>
                                                <option value="Medium">Medium</option>
                                                <option value="Low">Low</option>
                                        </Field>   
                                    </fieldset>
                                    <button type='submit' className='btn btn-success'>Update</button>          
                                </Form>
                                
                            )
                        }
                    </Formik>

                </div>
            </div>
        );
    }
} 
export default UpdateTask;
