import axios from 'axios';
import React, { Component } from 'react'
class LoginComponent extends Component{
    constructor(props){
        super(props)
        this.state={
            users:[],
            username:'',
            password:'',
            userId:10001,
            hasFailed:false
            
        }
    }
    handleWelcome=()=>{
        axios.get("http://localhost:8080/myusers")
        .then(response=>{this.setState({users:response.data})})
    }
    componentDidMount(){
        this.handleWelcome()
    }
    handleLogin=()=>{
        // console.log(e.)
        this.state.users.map(user=>{

            if(this.state.username===user.user_name && this.state.password===user.password){ 
                alert('Login Successful')
                console.log(user.user_id)
                this.setState({userId:user.user_id})
                // let user1=user.user_id;
                this.props.history.push(`/tasks/${this.state.userId}`)
                this.setState({hasFailed:false})
                // console.log(this.state.SuccessMsg)
                // console.log(user.user +" "+ user.password)
                // console.log(this.state)
            }else{
                console.log(this.state.username+" "+this.state.password)
                this.setState({hasFailed:true})
                console.log("Login Failed")
            }
        }
        )
        // this.props.history.push("/users")
        // axios.post("http://localhost:8080/users",)
    }
    handleChange=(event)=>{
        this.setState({[event.target.name]:event.target.value})
    }
    handleReset=()=>{
        this.setState({username:'',password:''})
    }
    render(){
        return(
            <div>
                    <h1>Welcome to Task Management System</h1>
                    <h2><em>Login Here....</em></h2>
                    {this.state.hasFailed && <h1>Invalid Credentials</h1>}
                    <br/>
                    <label htmlFor='username'>Username:</label>
                    <input type='text' name='username' placeholder='Enter name here' value={this.state.username} onChange={this.handleChange}/><br/>
                    <br/>
                    <label htmlFor='password'>Password:</label>
                    <input type='password' name='password' placeholder='Enter password here' value={this.state.password} onChange={this.handleChange}/><br></br>
                    <br/>
                    <button onClick={this.handleLogin} className='btn btn-outline-dark'>Submit</button>
                    <button onClick={this.handleReset} className='btn btn-outline-warning' style={{'margin':'10px'}}>Reset</button>
                    <br/><a style={{'textDecoration':'none','color':'black'}} href="https://www.google.com">New User? SignUp Here</a>
            </div>
        );
    }
}
export default LoginComponent;
