class RoleService{
    setRole(myRole){
        sessionStorage.setItem('authenticatedRole',myRole);
        console.log(role);
    }
    
    getRole(){
        let role=sessionStorage.getItem('authenticatedRole');
        return role;
    }
    
}
export default new RoleService();
