import axios from "axios";
import React, { Component } from "react";
import {Field, Form, Formik} from 'formik';
import AuthenticationService from "./AuthenticationService";
class UpdateTask extends Component{
    constructor(props){
        super(props);
        this.state={
            taskid:this.props.match.params.taskId,
            utask:{
                status:'',
                priority:'',
                notes:''
            },
            task1:{}
        }
    }
    // handleChange=(e)=>{
    //     this.setState({[e.target.name]:e.target.value})
    // }
    handleTasks=()=>{
        let task=this.state.taskid
        axios.get(`http://localhost:8080/allTasks/${task}`)
        .then(response=>{console.log(response.data);this.setState({task1:response.data})})

        console.log(this.state.task1.creatorId)
    }
    componentDidMount(){
        this.handleTasks();
    }
    handleSubmit=(values)=>{
        this.setState(prevState => {
            let utask = Object.assign({}, prevState.utask);  // creating copy of state variable jasper
            utask.status = values.status;                     // update the name property, assign a new value                 
            utask.priority=values.priority;
            utask.notes=values.notes;
            return { utask };                                 // return new object jasper object
          })
        console.log(this.state.utask)
        axios.put(`http://localhost:8080/tasks/${this.state.taskid}`,this.state.utask)
        .then(response=>{alert(response.data)})

        this.props.history.push(`/tasks/${AuthenticationService.getUserId()}`)
        window.location.reload()

        // this.handleClear()
    }
    // handleClear=()=>{
    //     this.setState(prevState => {
    //         let utask = Object.assign({}, prevState.utask); 
    //         utask.status = '';                   
    //         utask.priority='';
    //         utask.notes='';
    //         return { utask };
    //       })
    // }
    render(){
        let status=this.state.task1.status;
        let priority=this.state.task1.priority;
        let notes=this.state.task1.notes;
        return(
            <div>
                <h1><em>Update Your Task Here...{this.state.taskid}</em></h1>
                <div className='container'>
                    <Formik
                        initialValues={{status,priority,notes}}
                        onSubmit={this.handleSubmit}
                    >
                        {
                            (props)=>(
                                <Form >
                                    {/* {console.log(status)} */}
                                    <br/>
                                    {/* {console.log(creatorId)} */}
                                    <fieldset className='form-group'>
                                        <label>Creator ID</label>
                                        <Field type='number' className='form-control' name='creatorId' value={this.state.task1.creatorId} readOnly/>
                                    </fieldset>
                                    <br/>
                                    <fieldset className='form-group'>
                                        <label>Task Name</label>
                                        <Field type='text' className='form-control' name='name' value={this.state.task1.name} readOnly/>
                                    </fieldset>
                                    <br/>
                                    <fieldset className='form-group'>
                                        <label>Task Description</label>
                                        <Field type='text' className='form-control' name='description' value={this.state.task1.description} readOnly/>
                                    </fieldset>
                                    <br/>
                                    <fieldset className='form-group'>
                                        <label>Status Update</label>
                                        <Field as="select" name="status" className='form-control' required>
                                                <option value="">select--</option>
                                                <option value="In progress">In progress</option>
                                                <option value="Completed">Completed</option>
                                        </Field>   
                                    </fieldset>
                                    <br/>
                                    <fieldset className='form-group'>
                                        <label>Priority Update</label>
                                        <Field as="select" name="priority" className='form-control' required>
                                                <option value="">select--</option>
                                                <option value="High">High</option>
                                                <option value="Medium">Medium</option>
                                                <option value="Low">Low</option>
                                        </Field>   
                                    </fieldset>
                                    <br/>
                                    <fieldset className='form-group'>
                                        <label>Add Notes</label>
                                        <Field as='textarea' name='notes' className='form-control' defaultValue={notes} />
                                    </fieldset>
                                    <br/>
                                    <button type='submit' className='btn btn-outline-success' style={{'margin':'20px'}}>Update</button>
                                </Form>
                                
                            )
                        }
                    </Formik>

                </div>
            </div>
        );
    }
} 
export default UpdateTask;
