import axios from "axios";
import React, { Component } from "react";
import {moment} from "moment";
import {Field, Form, Formik} from 'formik';
class UpdateTask extends Component{
    constructor(props){
        super(props);
        this.state={
            taskid:this.props.match.params.taskId,
            utask:{
                status:'',
                priority:'',
                notes:''
            },
            statusChangedOn:new Date(),
            task1:{},
        }
    }
    handleChange=(e)=>{
        this.setState({[e.target.name]:e.target.value})
    }
    handleTasks=()=>{
        let task=this.state.taskid
        axios.get(`http://localhost:8080/allTasks/${task}`)
        .then(response=>{console.log(response.data);this.setState({task1:response.data})})
    }
    componentDidMount(){
        this.handleTasks();
    }
    getDate=()=>{
        return moment();
    }
    render(){
            let creatorId=this.state.task1.creatorId
            let tname=this.state.task1.name
            let description=this.state.task1.description
        return(
            <div>
                <h1><em>Update Your Task Here...{this.state.taskid}</em></h1>
                <div className='container'>
                    <Formik
                    initialValues={{creatorId,tname,description}}>
                        {
                            (props)=>(
                                <Form >
                                    <br/>
                                    <fieldset className='form-group'>
                                        <label>Creator ID</label>
                                        <Field type='number' className='form-control' name='creatorId'/>
                                    </fieldset>
                                    <br/>
                                    <fieldset className='form-group'>
                                        <label>Task Name</label>
                                        <Field type='text' className='form-control' name='tname'/>
                                    </fieldset>
                                    <br/>
                                    <fieldset className='form-group'>
                                        <label>Task Description</label>
                                        <Field type='text' className='form-control' name='description'/>
                                    </fieldset>
                                    <br/>
                                    <fieldset className='form-group'>
                                        <label>Status Update</label>
                                        <Field as="select" name="status" className='form-control'>
                                                <option value="">select--</option>
                                                <option value="New">New</option>
                                                <option value="In progress">In progress</option>
                                                <option value="Completed">Completed</option>
                                        </Field>   
                                    </fieldset>
                                    <br/>
                                    <fieldset className='form-group'>
                                        <label>Priority Update</label>
                                        <Field as="select" name="status" className='form-control'>
                                                <option value="">select--</option>
                                                <option value="High">High</option>
                                                <option value="Medium">Medium</option>
                                                <option value="Low">Low</option>
                                        </Field>   
                                    </fieldset>
                                    <br/>
                                    <button type='submit' className='btn btn-success'>Update</button>          
                                </Form>
                                
                            )
                        }
                    </Formik>

                </div>
            </div>
        );
    }
} 
export default UpdateTask;
