import React, { Component } from "react";
import CounterButton from "./CounterButton";
class Counter extends Component{
    constructor(){
        super()
        this.state={
            counter:0
        }
    }
    increment=()=>{
        console.log('increment from the parent method')
    }
    render(){
        return (
            <div className="counter1">
              <CounterButton />
              <CounterButton by={5}/>
              <CounterButton by={10}/>
            </div>
          );
    }    
}

export default Counter;
