import React, { Component } from "react";
import axios from 'axios';
// import { Link } from "react-router-dom";
class GetTasksDemo extends Component{
    constructor(props){
        super(props)
        this.state={
            tasks:[],
            // ownerId:this.props.match.params.user_id
        }
    }
    handleTask=()=>{
        // let owner_id=this.state.ownerId;
        axios.get('http://localhost:8080/tasks')
        .then(response=>this.setState({tasks:response.data}))
    }
    componentDidMount(){
        this.handleTask()

    }
    render(){
        return(
            <div className='container-fluid' style={{'padding':'30px'}}>

                <h1>List of Tasks</h1>
                    {/* <div style={{'textAlign':'right','margin':'20px'}}><Link to='/addTask' style={{'textDecoration':'none','color':'black'}}>Add task here...</Link></div> */}
                    <div className='row'>
                        {this.state.tasks.map(task=>
                                <div className='col-5'>
                                <table className='table table-bordered border-primary'>
                                    <tr>
                                        <td>Task name:</td>
                                        <td>{task.name}</td>
                                    </tr>
                                    <tr>
                                        <td>Task description:</td>
                                        <td>{task.description}</td>
                                    </tr>
                                    <tr>
                                        <td>Task priority:</td>
                                        <td>{task.priority}</td>
                                    </tr>
                                    <tr>
                                        <td>Task status:</td>
                                        <td>{task.status}</td>
                                    </tr>
                                </table>
                            </div>
                            )}
                        {/* <div className='col-3'>
                            <table>
                                <tr>
                                    <td>Task name:</td>
                                    <td>task2</td>
                                </tr>
                            </table>
                        </div> */}
                    </div>
                    {/* <button onClick={this.handleClick}>Click Me!</button>
                    <h1>{this.state.name}</h1> */}
            </div>
        );
    }
} 
export default GetTasksDemo;
