import axios from "axios";
import React, { Component } from "react";
class UpdateTask extends Component{
    constructor(props){
        super(props);
        this.state={
            taskid:this.props.match.params.taskId,
            utask:{
                status:''
            },
            task1:{}
        }
    }
    handleTasks=()=>{
        let task=this.state.taskid
        axios.get(`http://localhost:8080/allTasks/${task}`)
        .then(response=>{console.log(response.data);this.setState({task1:response.data})})
    }
    componentDidMount(){
        this.handleTasks();
    }
    render(){
        return(
            <div>
                <h1><em>Update Your Task Here...{this.state.taskid}</em></h1>
                <table>
                    <tbody>
                        <tr>
                            <td><label htmlFor='creator_id'>Creator Id: </label></td>
                            <td><input type='text' name='creator_id' value={this.state.task1.creatorId}/></td>
                        </tr>
                        <tr>
                            <td><label htmlFor='name'>Task Name: </label></td>
                            <td><input type='text' name='name' value={this.state.task1.name}/></td>
                        </tr>
                        <tr>
                            <td><label htmlFor='description'>Task Description: </label></td>
                            <td><textarea name='description' value={this.state.task1.description}/></td>
                        </tr>
                    </tbody>
                </table>
            </div>
        );
    }
} 
export default UpdateTask;
