import axios from 'axios';
import React, { Component } from 'react'
class LoginComponent extends Component{
    constructor(props){
        super(props)
        this.state={
            users:[],
            username:'',
            password:'',
            hasFailed:false,
            SuccessMsg:false
            
        }
    }
    componentDidMount(){
        axios.get("http://localhost:8080/users")
        .then(response=>{console.log(response.data);this.setState({users:response.data})})
    }
    handleLogin=()=>{
        // console.log(e.)
        this.state.users.map(user=>{

            if(this.state.username===user.user && this.state.password===user.password){ 
                this.setState({SuccessMsg:true})
            }else{
                this.setState({SuccessMsg:false,hasFailed:true})
                console.log("Login Failed")
            }
        }
        )
        // this.props.history.push("/users")
        // axios.post("http://localhost:8080/users",)
    }
    handleChange=(event)=>{
        this.setState({[event.target.name]:event.target.value})
    }
    render(){
        return(
            <div>
                    {this.state.hasFailed && <h1>Invalid Credentials</h1>}
                    {this.state.SuccessMsg && <h1>Login Success</h1>}
                    Name:<input type='text' name='username' placeholder='Enter name here' value={this.state.username} onChange={this.handleChange}/><br/>
                    Password:<input type='password' name='password' placeholder='Enter password here' value={this.state.password} onChange={this.handleChange}/><br></br>
                    <button onClick={this.handleLogin}>Submit</button>
            </div>
        );
    }
}
export default LoginComponent;
